import React, { useState } from 'react';
//Styles
import './Alert.css';

const Alert = (message, open='block') => {
    const [alertOpen, setAlertOpen] = useState(open)
    const handleSubmit = () => {
        setAlertOpen('none');
    }

    return (

        <div className="alert__container" style={{display:alertOpen}}>
            <div className="alert__content">
                <div className="alert__message">
                    <h2>{message}</h2>
                </div>
                <button className="alert__btn" onSubmit={handleSubmit} >OK</button>
            </div>
        </div>
    )
}

export default Alert;
