import React, { useState } from 'react';
import { Link } from 'react-router-dom';

import { Button, Form, FormGroup, Input, Label } from 'reactstrap';
//Styles
import { Wrapper, Content, Brand, NavList, Hidden, LoginForm } from './Header.styles';
//Images, Icons
import  logo  from '../../images/logo.png';
import { faBars, faUserCircle, faTimes } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const Header = ({color, barColor}) => {
    const [navOpen, setNavOpen] = useState("none");
    const [loginOpen, setLoginOpen] = useState("none");

    const toggleLogin = () => { 
        console.log("modal toggle working")
        if (loginOpen === "none") {
            setLoginOpen("block");
        } else {
            setLoginOpen("none");
        }
    }

    const toggleNav = () => { 
        console.log("nav working");
        if (navOpen === "none") {
            setNavOpen("block");
        } else {
            setNavOpen("none");
        }
    }

    const handleLogin = () => { 
        console.log("login working");
    }


    return (
    <>
        <Wrapper>
            <Content color={color} barColor={barColor}>
                <Brand color={color}><a href="/"><img src={logo} alt="Logo"/><h2 className="brandName" style={{textDecoration: "none"}} >trans!a</h2></a></Brand>
                <NavList color={color} barColor={barColor}>
                    <div className="navbar">
                        <ul>
                            <li className="navItem"><a href="/">Home</a><div className="underline"></div></li>
                            <li className="navItem"><a href="/services">Services</a><div className="underline"></div></li>
                            <li className="navItem"><a href="/team">Team</a><div className="underline"></div></li>
                            <li className="navItem"><a href="/about">About Us</a><div className="underline"></div></li>
                            <li className="navItem"><a href="/contact">Contact</a><div className="underline"></div></li>
                        </ul>
                    </div>
                    <Button className="quoteBtn"><Link to='/quote'>quote</Link></Button>
                    <FontAwesomeIcon icon={faUserCircle} className="login" onClick={toggleLogin} />
                    <FontAwesomeIcon icon={faBars} className="toggler" onClick={toggleNav}/>
                </NavList>
            </Content>
        </Wrapper>

        <Hidden style={{display: navOpen}}>
            <div className="container">
                <div className="mr auto">
                    <ul>
                        <li className="navItem"><a href="/">Home</a></li>
                        <li className="navItem"><a href="/services">Services</a></li>
                        <li className="navItem"><a href="/team">Team</a></li>
                        <li className="navItem"><a href="/about">About Us</a></li>
                        <li className="navItem"><a href="/contact">Contact</a></li>
                    </ul>
                </div>
            </div>
        </Hidden>

        <LoginForm style={{display:loginOpen}}>
            <div className="wrapper">
                <div className="content">
                    <div className="row">
                        <h2 className="col-12">
                            Welcome Back!
                            <FontAwesomeIcon icon={faTimes} onClick={toggleLogin} />
                        </h2>
                        <hr />
                        <Form onSubmit={handleLogin} className="formBody">
                            <FormGroup className="formInput">
                                <Label htmlFor="username" className="label">Username</Label>
                                <Input type="text" id="username" name="username" /* innerRef={input => username = input} */ />
                            </FormGroup>
                            <FormGroup className="formInput">
                                <Label htmlFor="password" className="label">Password</Label>
                                <Input type="password" id="password" name="password" /*innerRef={input => password = input} */ />
                            </FormGroup>
                            <FormGroup check className="formInput">
                                <Label check className="formCheck">
                                    <Input type="checkbox" name="remember" className="checkbox"/* innerRef={input => remember = input} */ />
                                    <p className="label">remember me</p>
                                </Label>
                            </FormGroup>
                            <Button type="submit" value="submit" color="primary">LOGIN</Button>
                        </Form>
                    </div>
                </div>
            </div>
        </LoginForm>
    </>
    );

};

export default Header;
